import React from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { useState, useEffect } from "react";
import { Formik, Form, Field } from "formik";
import * as Yup from "yup"
function Post() {
  let { id } = useParams();
  const [listComment,setCommentObject]=useState([]);
  const [postObject, setPostObject] = useState([]);
  useEffect(() => {
    axios.get(`http://localhost:3004/posts/byId/${id}`).then((response) => {
      setPostObject(response.data);
    axios.get(`http://localhost:3004/comments/${id}`).then((response)=>{
      setCommentObject(response.data);
    })
    });
  });
  const userComment = {
    commentBody: "",
  };
  const validationSchema = Yup.object().shape({
    commentBody: Yup.string().required(),
  });
  const submit = (data) => {
    axios.post("http://localhost:3004/comment", data).then((response) => {
      console.log("comment added with success");
    });
  };
  return (
    //basic html code
    <div className="pagePost">
    <div className="postOnPostPage">
      <div className="upperPost">
        <div>
          <span className="image"></span>
        </div>
        <div className="postData">
          <div className="title">{postObject.title}</div>
          <div className="username">{postObject.username}</div>
        </div>
      </div>
      <div className="postText">{postObject.postText}</div>
      {postObject && postObject.createdAt && (
  <div className="time">{postObject.createdAt.slice(0, 10)}</div>
)}
    </div>
    <div className="commentSection">
      <div className="comments">
        {/* Using Get Request Here you Post the comments */
        listComment.map((value,key)=>{
          return (<div className="comment">
            {value.commentBody};
          </div>)
        })
        }
      </div>
      <div className="addComment">
      <Formik
        initialValues={userComment}
        onSubmit={submit}
        validationSchema={validationSchema}
      >
        <Form className="form">
          <Field id="inputComment" name="commentBody" placeholder="Comment on post..." autocomplete="off"/>
          <button type="submit">Add Comment</button>
        </Form>
      </Formik>
      </div>
    </div>
    </div>
  );
}
export default Post;
