import React from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { useState, useEffect } from "react";
import { Formik, Form, Field } from "formik";
import * as Yup from "yup";
import ReactDOM from "react-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faRefresh, faTrash } from "@fortawesome/free-solid-svg-icons";
import { useNavigate } from "react-router-dom";
function Post() {
  let { id } = useParams();
  const [listComment, setComments] = useState([]);
  const [postObject, setPostObject] = useState([]);
  const avigate = useNavigate();
  useEffect(() => {
    const fetchData = async () => {
      try {
        const postResponse = await axios.get(
          `http://localhost:3004/posts/byId/${id}`
        );
        setPostObject(postResponse.data);
        const commentsResponse = await axios.get(
          `http://localhost:3004/comments/${id}`
        );
        setComments(commentsResponse.data);
      } catch (error) {
        console.error("Error fetching data", error);
      }
    };
    fetchData();
  }, [id]); // by including [id] in the dependency array you achieve the preveting from unnecessary requests.
  const userComment = {
    commentBody: "",
    PostId: id,
  };
  const validationSchema = Yup.object().shape({
    commentBody: Yup.string().required(),
  });
  const addCommentSubmit = async (data, { resetForm }) => {
    axios.post("http://localhost:3004/comments", data).then((response) => {
      resetForm();
      const commentToAdd = { commentBody: data.commentBody };
      setComments([...listComment, commentToAdd]);
    });
  };
  return (
    //basic html code
    <div className="pagePost">
      <div className="postContainer">
        <div className="postOnPostPage">
          <div className="upperPost">
            <div>
              <span className="image"></span>
            </div>
            <div className="postData">
              <div className="title">{postObject.title}</div>
              <div className="username">{postObject.username}</div>
            </div>
          </div>
          <div className="postText">{postObject.postText}</div>
          {postObject && postObject.createdAt && (
            <div className="time">{postObject.createdAt.slice(0, 10)}</div>
          )}
        </div>
        <div className="commentSection">
          <div className="comments">
            <p>Comments</p>
            {listComment.map((value, key) => {
              return (
                <div className="comment">
                  <div key={key}>
                    {value.commentBody}
                    <button
                      onClick={() => {
                        axios.delete(
                          `http://localhost:3004/comments/deleteComment/${value.id}`
                        );
                      }}
                    >
                      {" "}
                      <FontAwesomeIcon icon={faTrash} />
                    </button>
                  </div>
                </div>
              );
            })}
          </div>
          <div className="addComment">
            <Formik
              initialValues={userComment}
              onSubmit={addCommentSubmit}
              validationSchema={validationSchema}
            >
              <Form>
                <div className="commentForm">
                  <Field
                    id="inputComment"
                    name="commentBody"
                    placeholder="Comment on post..."
                    autocomplete="off"
                  />
                  <button type="submit">Publish</button>
                </div>
              </Form>
            </Formik>
          </div>
        </div>
      </div>
    </div>
  );
}
export default Post;
