import "./App.css";
import axios from "axios";
import { useEffect } from "react";
import { useState } from "react";
//the 'useState' hook allows us to create variables that can hold data and be updated overtime
function App() {
  //listOfPosts is var that holds the current value of the state,starts as an empty array'[]'
  //setListOfPosts is a function you can use to update the 'listOfPosts' value
  const [listOfPosts, setListOfPosts] = useState([]);
  useEffect(() => {
    //Running the logic we want to pass after refreshing
    //using axios to run a getRequest that you can see in insomnia
    axios.get("http://localhost:3001/posts").then((response) => {
       setListOfPosts(response.data);
    });
  }, []);
  return (//你看这是JSX code the return of divs in javascript code
  <div className="body">
    <div></div>{listOfPosts.map((value,key)=>{
    return (<div className="post"></div>);
  })}</div>);
}
export default App;
