import React from "react";
import axios from "axios";
import { useEffect } from "react";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useContext } from "react";
import { AuthContext } from "../helpers/AuthContext";
import { post } from "../../../server/routes/Posts";
function Home() {
  let navigate = useNavigate();
  const { authState } = useContext(AuthContext);
  //listOfPosts is var that holds the current value of the state,starts as an empty array'[]'
  //setListOfPosts is a function you can use to update the 'listOfPosts' value
  const [listOfPosts, setListOfPosts] = useState([]);
  useEffect(() => {
    //Running the logic we want to pass after refreshing
    //using axios to run a getRequest that you can see in insomnia
    axios.get("http://localhost:3004/posts").then((response) => {
      setListOfPosts(response.data);
    });
  }, []);
  const deletePost = (postId)=>{
    axios
    .delete(`http://localhost:3004/posts/${postId}`,{headers:{accessToken:localStorage.getItem("accessToken")}})
    .then((response)=>{
      setListOfPosts(listOfPosts.fil)
    })
  }
  return (
    //你看这是JSX code the return of divs in javascript code
    <div className="container">
      {[...listOfPosts].reverse().map((value, key) => {
        return (
          //basic html code
          <div
            className="post"
            onClick={() => {
              navigate(`/post/${value.id}`);
            }}
          >
            <div className="upperPost">
              <div>
                <span className="image"></span>
              </div>
              <div className="postData">
                <div className="title">{value.title}</div>
                <div className="username">{value.username}</div>
              </div>
            </div>
            <div className="postText">{value.postText}</div>
            <div className="time">{value.createdAt.slice(0, 10)}</div>
            {authState.username === value.username && <button>X</button>}
          </div>
        );
      })}
    </div>
  );
}
export default Home;
