import React from "react";
import Home from "./pages/Home";
import "./App.css";
import { BrowserRouter as Router, Route, Link, Routes } from "react-router-dom";
import CreatePost from "./pages/CreatePost";
import Post from "./pages/Post";
import Login from "./pages/Login";
import Registration from "./pages/Registration";
//the 'useState' hook allows us to create variables that can hold data and be updated overtime
const {AuthContext} = require
import { useState } from "react";
function App() {
  const handleLogout = () => {
    localStorage.removeItem("accessToken");
  };
  //authState:Status of user (logged in or no)
  //setAuthState: Helps use to change the status of user
  //Status initialized as NOT LOGGED IN
  const [authState, setAuthState] = useState(false);
  return (
    <div className="App">
      <AuthContext.Provider value={{ authState, setAuthState }}>
        <Router>
          <div className="linkDiv">
            {/* (true) && (true) */}
            {/* (false) && (false) */}
            {!authState && (
              <>
                {" "}
                <Link to="/login" className="Link">
                  Login
                </Link>
                <Link to="/registration" className="Link">
                  Registration
                </Link>
              </>
            )}
            <Link to="/createpost" className="Link">
              Create A Post
            </Link>
            <Link className="Link" to="/">
              Home Page
            </Link>
            <Link className="Link" onClick={handleLogout} to="/login">
              Log Out
            </Link>
          </div>
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/createpost" element={<CreatePost />} />
            <Route path="/post/:id" element={<Post />} />
            <Route path="/login" element={<Login />} />
            <Route path="/registration" element={<Registration />} />
          </Routes>
        </Router>
      </AuthContext.Provider>
    </div>
  );
}
export default App;
